ItemsBlogs itemsBlogsFromJson(String str) => ItemsBlogs.fromJson(json.decode(str));
String itemsBlogsToJson(ItemsBlogs data) => json.encode(data.toJson());
class ItemsBlogs {
  ItemsBlogs({
      String? id, 
      String? url, 
      String? title, 
      String? contentHtml, 
      String? contentText, 
      String? datePublished, 
      String? dateModified, 
      List<Authors>? authors, 
      Author? author, 
      String? image, 
      List<String>? tags,}){
    _id = id;
    _url = url;
    _title = title;
    _contentHtml = contentHtml;
    _contentText = contentText;
    _datePublished = datePublished;
    _dateModified = dateModified;
    _authors = authors;
    _author = author;
    _image = image;
    _tags = tags;
}

  ItemsBlogs.fromJson(dynamic json) {
    _id = json['id'];
    _url = json['url'];
    _title = json['title'];
    _contentHtml = json['content_html'];
    _contentText = json['content_text'];
    _datePublished = json['date_published'];
    _dateModified = json['date_modified'];
    if (json['authors'] != null) {
      _authors = [];
      json['authors'].forEach((v) {
        _authors?.add(Authors.fromJson(v));
      });
    }
    _author = json['author'] != null ? Author.fromJson(json['author']) : null;
    _image = json['image'];
    _tags = json['tags'] != null ? json['tags'].cast<String>() : [];
  }
  String? _id;
  String? _url;
  String? _title;
  String? _contentHtml;
  String? _contentText;
  String? _datePublished;
  String? _dateModified;
  List<Authors>? _authors;
  Author? _author;
  String? _image;
  List<String>? _tags;
ItemsBlogs copyWith({  String? id,
  String? url,
  String? title,
  String? contentHtml,
  String? contentText,
  String? datePublished,
  String? dateModified,
  List<Authors>? authors,
  Author? author,
  String? image,
  List<String>? tags,
}) => ItemsBlogs(  id: id ?? _id,
  url: url ?? _url,
  title: title ?? _title,
  contentHtml: contentHtml ?? _contentHtml,
  contentText: contentText ?? _contentText,
  datePublished: datePublished ?? _datePublished,
  dateModified: dateModified ?? _dateModified,
  authors: authors ?? _authors,
  author: author ?? _author,
  image: image ?? _image,
  tags: tags ?? _tags,
);
  String? get id => _id;
  String? get url => _url;
  String? get title => _title;
  String? get contentHtml => _contentHtml;
  String? get contentText => _contentText;
  String? get datePublished => _datePublished;
  String? get dateModified => _dateModified;
  List<Authors>? get authors => _authors;
  Author? get author => _author;
  String? get image => _image;
  List<String>? get tags => _tags;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['id'] = _id;
    map['url'] = _url;
    map['title'] = _title;
    map['content_html'] = _contentHtml;
    map['content_text'] = _contentText;
    map['date_published'] = _datePublished;
    map['date_modified'] = _dateModified;
    if (_authors != null) {
      map['authors'] = _authors?.map((v) => v.toJson()).toList();
    }
    if (_author != null) {
      map['author'] = _author?.toJson();
    }
    map['image'] = _image;
    map['tags'] = _tags;
    return map;
  }

}

